import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker, Popup, countries,GeoJSON } from "react-leaflet";
import axios from "axios";

const Map = () => {
  const [selectedCountry, setSelectedCountry] = useState(null);
  const [countryDetails, setCountryDetails] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");

  useEffect(() => {
    if (selectedCountry) {
      fetchCountryDetails(selectedCountry);
    }
  }, [selectedCountry]);

  const fetchCountryDetails = async (countryCode) => {
    try {
      const response = await axios.get(
        `https://restcountries.com/v3.1/alpha/${countryCode}`
      );
      setCountryDetails(response.data);
    } catch (error) {
      console.error("Error fetching country details:", error);
      // Handle error and show a user-friendly message
    }
  };

  const handleCountryClick = (e) => {
    const countryCode = e.target.feature.properties.iso_a2;
    setSelectedCountry(countryCode);
  };

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    // Perform search based on the query
    // For simplicity, let's assume the query is the country name
    const country = countries.find(
      (country) =>
        country.name.common.toLowerCase() === searchQuery.toLowerCase()
    );
    if (country) {
      setSelectedCountry(country.cca2);
      setSearchQuery("");
    } else {
      // Handle error if the country is not found
    }
  };

  return (
    <div>
      <h1>World Map</h1>
      <form onSubmit={handleSearchSubmit}>
        <input
          type="text"
          placeholder="Search for a country"
          value={searchQuery}
          onChange={handleSearchChange}
        />
        <button type="submit">Search</button>
      </form>
      <MapContainer
        center={[0, 0]}
        zoom={2}
        style={{ height: "400px", width: "100%" }}
      >
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        <GeoJSON data={countriesGeoJSON} onClick={handleCountryClick} />
      </MapContainer>
      {countryDetails && (
        <div>
          <h2>{countryDetails.name.common}</h2>
          <p>Capital: {countryDetails.capital}</p>
          <p>Population: {countryDetails.population}</p>
          <p>Area: {countryDetails.area} sq km</p>
          <p>Languages: {Object.values(countryDetails.languages).join(", ")}</p>
          <p>
            Currency:{" "}
            {
              countryDetails.currencies[
                Object.keys(countryDetails.currencies)[0]
              ].name
            }
          </p>
        </div>
      )}
    </div>
  );
};

export default Map;
