import React, { useState } from "react";
import { MapContainer, TileLayer, useMapEvents } from "react-leaflet";
import axios from "axios";
import CountryDetails from "./components/CountryDetails";

const App = () => {
  const [selectedCountry, setSelectedCountry] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");

  const handleCountryClick = async (event) => {
    const { latlng } = event;
    const response = await axios.get(
      `https://restcountries.com/v2/latlng/${latlng.lat},${latlng.lng}`
    );
    const countryData = response.data[0];
    setSelectedCountry(countryData);
    console.log()
  };

  const handleSearch = async () => {
    if (searchQuery.trim() === "") {
      // Handle empty search query if needed
      return;
    }

    const response = await axios.get(
      `https://restcountries.com/v2/name/${searchQuery}`
    );

    if (response.data.length > 0) {
      const countryData = response.data[0];
      setSelectedCountry(countryData);
      console.log(response.data);
    } else {
      // Handle no results found if needed
      setSelectedCountry(null);
    }
  };

  const ClickEvents = () => {
    useMapEvents({
      click: handleCountryClick,
    });
    return null;
  };

  return (
    <div>
      <div>
        <input
          type="text"
          placeholder="Search country..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>

      </div>
      <MapContainer
        center={[0, 0]}
        zoom={2}
        style={{ height: "100vh", width: "100%" }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="Map data &copy; OpenStreetMap contributors"
        />
        <ClickEvents />
      </MapContainer>
      {selectedCountry && <CountryDetails country={selectedCountry} />}
    </div>
  );
};

export default App;
