import React, { useState, useEffect } from "react";
import axios from "axios";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";

const CountrySearch = () => {
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedCountry, setSelectedCountry] = useState(null);
  const [mapCenter, setMapCenter] = useState([0, 0]);

  useEffect(() => {
    if (selectedCountry) {
      const { latlng } = selectedCountry;
      setMapCenter([latlng[0], latlng[1]]);
    }
  }, [selectedCountry]);

  const handleSearch = async () => {
    if (searchQuery.trim() === "") {
      // Handle empty search query if needed
      return;
    }

    try {
      const response = await axios.get(
        `https://restcountries.com/v2/name/${searchQuery}`
      );

      if (response.data.length > 0) {
        const countryData = response.data[0];
        setSelectedCountry(countryData);
      } else {
        // Handle no results found if needed
        setSelectedCountry(null);
      }
    } catch (error) {
      // Handle error if the API request fails
      console.log(error);
      setSelectedCountry(null);
    }
  };

  const handleChange = (e) => {
    setSearchQuery(e.target.value);
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Enter a country name"
        value={searchQuery}
        onChange={handleChange}
      />
      <button onClick={handleSearch}>Search</button>

      {selectedCountry ? (
        <div>
          <h2>{selectedCountry.name}</h2>
          <p>Capital: {selectedCountry.capital}</p>
          <p>Population: {selectedCountry.population}</p>
          <p>Area: {selectedCountry.area} square kilometers</p>
          <p>
            Languages:{" "}
            {selectedCountry.languages.map((lang) => lang.name).join(", ")}
          </p>
          <p>
            Currencies:{" "}
            {selectedCountry.currencies.map((curr) => curr.name).join(", ")}
          </p>

          <MapContainer center={mapCenter} zoom={5} style={{ height: "400px" }}>
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution="Map data &copy; OpenStreetMap contributors"
            />
            <Marker position={mapCenter}>
              <Popup>{selectedCountry.name}</Popup>
            </Marker>
          </MapContainer>
        </div>
      ) : (
        <p>No country selected</p>
      )}
    </div>
  );
};

export default CountrySear;
